using System;
using System.IO;
using System.Threading;
using System.Threading.Tasks;
using HtmlToOpenXml.IO;

namespace HtmlToOpenXml.Tests
{
    sealed class LocalWebRequest : IWebRequest
    {
        public void Dispose()
        {
        }

        public Task<Resource> FetchAsync(Uri requestUri, CancellationToken cancellationToken)
        {
            if (!requestUri.IsAbsoluteUri && requestUri.OriginalString == "/img/black-dot")
            {
                return Task.FromResult(new Resource() {
                    Content = new MemoryStream(Convert.FromBase64String(@"")),
                    StatusCode = System.Net.HttpStatusCode.OK
                });
            }

            string filename = Path.GetFileName(requestUri.AbsoluteUri);
            if (!File.Exists("../../images/" + filename))
            {
                return null;
            }

            return Task.FromResult(new Resource() {
                Content = File.OpenRead("../../images/" + filename),
                StatusCode = System.Net.HttpStatusCode.OK
            });
        }

        public bool SupportsProtocol(string protocol)
        {
            return true;
        }
    }
}